              {/* <Route path="/project" component={Projects} exact></Route> */}
            </Switch>
          </Container>
        </main>

        <footer>
          <div className="text-center-1">Created by Amini</div>
        </footer>
      </div>
      {/* </div> */}
    </BrowserRouter>
  );
}

export default App;
